package hex.genmodel.easy.exception;

/**
 * Throw when prediction uses the wrong schema for model. User may not be aware that
 * the schema is 100% wrong.  User needs to know because a prediction is still
 * generated by the model.
 */
public class PredictBadSchemaException extends PredictException {
    public PredictBadSchemaException(String message) { super (message);}
}
